import { defineComponent, inject, onUnmounted, provide, reactive } from "vue";
import { useHead } from "#app/composables/head";
const HeadComponentCtxSymbol = Symbol("head-component");
const TagPositionProps = {
  /**
   * @deprecated Use tagPosition
   */
  body: { type: Boolean, default: void 0 },
  tagPosition: { type: String }
};
const normalizeProps = (_props) => {
  const props = Object.fromEntries(
    Object.entries(_props).filter(([_, value]) => value !== void 0)
  );
  if (typeof props.body !== "undefined") {
    props.tagPosition = props.body ? "bodyClose" : "head";
  }
  if (typeof props.renderPriority !== "undefined") {
    props.tagPriority = props.renderPriority;
  }
  return props;
};
function useHeadComponentCtx() {
  return inject(HeadComponentCtxSymbol, createHeadComponentCtx, true);
}
function createHeadComponentCtx() {
  const prev = inject(HeadComponentCtxSymbol, null);
  if (prev) {
    return prev;
  }
  const input = reactive({});
  const entry = useHead(input);
  const ctx = { input, entry };
  provide(HeadComponentCtxSymbol, ctx);
  return ctx;
}
const globalProps = {
  accesskey: String,
  autocapitalize: String,
  autofocus: {
    type: Boolean,
    default: void 0
  },
  class: { type: [String, Object, Array], default: void 0 },
  contenteditable: {
    type: Boolean,
    default: void 0
  },
  contextmenu: String,
  dir: String,
  draggable: {
    type: Boolean,
    default: void 0
  },
  enterkeyhint: String,
  exportparts: String,
  hidden: {
    type: Boolean,
    default: void 0
  },
  id: String,
  inputmode: String,
  is: String,
  itemid: String,
  itemprop: String,
  itemref: String,
  itemscope: String,
  itemtype: String,
  lang: String,
  nonce: String,
  part: String,
  slot: String,
  spellcheck: {
    type: Boolean,
    default: void 0
  },
  style: { type: [String, Object, Array], default: void 0 },
  tabindex: String,
  title: String,
  translate: String,
  /**
   * @deprecated Use tagPriority
   */
  renderPriority: [String, Number],
  /**
   * Unhead prop to modify the priority of the tag.
   */
  tagPriority: { type: [String, Number] }
};
export const NoScript = defineComponent({
  name: "NoScript",
  inheritAttrs: false,
  props: {
    ...globalProps,
    ...TagPositionProps,
    title: String
  },
  setup(props, { slots }) {
    const { input } = useHeadComponentCtx();
    input.noscript ||= [];
    const idx = input.noscript.push({}) - 1;
    onUnmounted(() => input.noscript[idx] = null);
    return () => {
      const noscript = normalizeProps(props);
      const slotVnodes = slots.default?.();
      const textContent = slotVnodes ? slotVnodes.filter(({ children }) => children).map(({ children }) => children).join("") : "";
      if (textContent) {
        noscript.innerHTML = textContent;
      }
      input.noscript[idx] = noscript;
      return null;
    };
  }
});
export const Link = defineComponent({
  name: "Link",
  inheritAttrs: false,
  props: {
    ...globalProps,
    ...TagPositionProps,
    as: String,
    crossorigin: String,
    disabled: Boolean,
    fetchpriority: String,
    href: String,
    hreflang: String,
    imagesizes: String,
    imagesrcset: String,
    integrity: String,
    media: String,
    prefetch: {
      type: Boolean,
      default: void 0
    },
    referrerpolicy: String,
    rel: String,
    sizes: String,
    title: String,
    type: String,
    /** @deprecated **/
    methods: String,
    /** @deprecated **/
    target: String
  },
  setup(props) {
    const { input } = useHeadComponentCtx();
    input.link ||= [];
    const idx = input.link.push({}) - 1;
    onUnmounted(() => input.link[idx] = null);
    return () => {
      input.link[idx] = normalizeProps(props);
      return null;
    };
  }
});
export const Base = defineComponent({
  name: "Base",
  inheritAttrs: false,
  props: {
    ...globalProps,
    href: String,
    target: String
  },
  setup(props) {
    const { input } = useHeadComponentCtx();
    onUnmounted(() => input.base = null);
    return () => {
      input.base = normalizeProps(props);
      return null;
    };
  }
});
export const Title = defineComponent({
  name: "Title",
  inheritAttrs: false,
  setup(_, { slots }) {
    const { input } = useHeadComponentCtx();
    onUnmounted(() => input.title = null);
    return () => {
      const defaultSlot = slots.default?.();
      input.title = defaultSlot?.[0]?.children ? String(defaultSlot?.[0]?.children) : void 0;
      if (import.meta.dev) {
        if (defaultSlot && (defaultSlot.length > 1 || defaultSlot[0] && typeof defaultSlot[0].children !== "string")) {
          console.error("<Title> can take only one string in its default slot.");
        }
      }
      return null;
    };
  }
});
export const Meta = defineComponent({
  name: "Meta",
  inheritAttrs: false,
  props: {
    ...globalProps,
    charset: String,
    content: String,
    httpEquiv: String,
    name: String,
    property: String
  },
  setup(props) {
    const { input } = useHeadComponentCtx();
    input.meta ||= [];
    const idx = input.meta.push({}) - 1;
    onUnmounted(() => input.meta[idx] = null);
    return () => {
      const meta = { "http-equiv": props.httpEquiv, ...normalizeProps(props) };
      if ("httpEquiv" in meta) {
        delete meta.httpEquiv;
      }
      input.meta[idx] = meta;
      return null;
    };
  }
});
export const Style = defineComponent({
  name: "Style",
  inheritAttrs: false,
  props: {
    ...globalProps,
    ...TagPositionProps,
    type: String,
    media: String,
    nonce: String,
    title: String,
    /** @deprecated **/
    scoped: {
      type: Boolean,
      default: void 0
    }
  },
  setup(props, { slots }) {
    const { input } = useHeadComponentCtx();
    input.style ||= [];
    const idx = input.style.push({}) - 1;
    onUnmounted(() => input.style[idx] = null);
    return () => {
      const style = normalizeProps(props);
      const textContent = slots.default?.()?.[0]?.children;
      if (textContent) {
        if (import.meta.dev && typeof textContent !== "string") {
          console.error("<Style> can only take a string in its default slot.");
        }
        input.style[idx] = style;
        style.textContent = textContent;
      }
      return null;
    };
  }
});
export const Head = defineComponent({
  name: "Head",
  inheritAttrs: false,
  setup: (_props, ctx) => {
    createHeadComponentCtx();
    return () => ctx.slots.default?.();
  }
});
export const Html = defineComponent({
  name: "Html",
  inheritAttrs: false,
  props: {
    ...globalProps,
    manifest: String,
    version: String,
    xmlns: String
  },
  setup(_props, ctx) {
    const { input } = useHeadComponentCtx();
    onUnmounted(() => input.htmlAttrs = null);
    return () => {
      input.htmlAttrs = { ..._props, ...ctx.attrs };
      return ctx.slots.default?.();
    };
  }
});
export const Body = defineComponent({
  name: "Body",
  inheritAttrs: false,
  props: globalProps,
  setup(_props, ctx) {
    const { input } = useHeadComponentCtx();
    onUnmounted(() => input.bodyAttrs = null);
    return () => {
      input.bodyAttrs = { ..._props, ...ctx.attrs };
      return ctx.slots.default?.();
    };
  }
});
<template>
  <div class="flex items-center gap-3">
    <span class="text-xs font-medium text-slate-400">Share:</span>
    <div class="flex items-center gap-2">
      <!-- Twitter Share -->
      <button
        @click="shareOnTwitter"
        class="inline-flex items-center justify-center w-8 h-8 rounded-full bg-slate-800/60 hover:bg-blue-600/20 border border-slate-700 hover:border-blue-500/50 text-slate-400 hover:text-blue-400 transition-all duration-200 cursor-pointer"
        title="Share on Twitter"
      >
        <svg class="w-4 h-4" fill="currentColor" viewBox="0 0 24 24">
          <path d="M18.244 2.25h3.308l-7.227 8.26 8.502 11.24H16.17l-5.214-6.817L4.99 21.75H1.68l7.73-8.835L1.254 2.25H8.08l4.713 6.231zm-1.161 17.52h1.833L7.084 4.126H5.117z"/>
        </svg>
      </button>

      <!-- LinkedIn Share -->
      <button
        @click="shareOnLinkedIn"
        class="inline-flex items-center justify-center w-8 h-8 rounded-full bg-slate-800/60 hover:bg-blue-700/20 border border-slate-700 hover:border-blue-600/50 text-slate-400 hover:text-blue-500 transition-all duration-200 cursor-pointer"
        title="Share on LinkedIn"
      >
        <svg class="w-4 h-4" fill="currentColor" viewBox="0 0 24 24">
          <path d="M20.447 20.452h-3.554v-5.569c0-1.328-.027-3.037-1.852-3.037-1.853 0-2.136 1.445-2.136 2.939v5.667H9.351V9h3.414v1.561h.046c.477-.9 1.637-1.85 3.37-1.85 3.601 0 4.267 2.37 4.267 5.455v6.286zM5.337 7.433c-1.144 0-2.063-.926-2.063-2.065 0-1.138.92-2.063 2.063-2.063 1.14 0 2.064.925 2.064 2.063 0 1.139-.925 2.065-2.064 2.065zm1.782 13.019H3.555V9h3.564v11.452zM22.225 0H1.771C.792 0 0 .774 0 1.729v20.542C0 23.227.792 24 1.771 24h20.451C23.2 24 24 23.227 24 22.271V1.729C24 .774 23.2 0 22.222 0h.003z"/>
        </svg>
      </button>

      <!-- Email Share -->
      <button
        @click="shareViaEmail"
        class="inline-flex items-center justify-center w-8 h-8 rounded-full bg-slate-800/60 hover:bg-emerald-600/20 border border-slate-700 hover:border-emerald-500/50 text-slate-400 hover:text-emerald-400 transition-all duration-200 cursor-pointer"
        title="Share via email"
      >
        <svg class="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M3 8l7.89 4.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z"/>
        </svg>
      </button>

      <!-- Copy Link -->
      <button
        @click="copyLink"
        class="inline-flex items-center justify-center w-8 h-8 rounded-full bg-slate-800/60 hover:bg-slate-600/20 border border-slate-700 hover:border-slate-500/50 text-slate-400 hover:text-slate-200 transition-all duration-200"
        :title="copied ? 'Copied!' : 'Copy link'"
      >
        <svg v-if="!copied" class="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M8 16H6a2 2 0 01-2-2V6a2 2 0 012-2h8a2 2 0 012 2v2m-6 12h8a2 2 0 002-2v-8a2 2 0 00-2-2h-8a2 2 0 00-2 2v8a2 2 0 002 2z"/>
        </svg>
        <svg v-else class="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M5 13l4 4L19 7"/>
        </svg>
      </button>
    </div>
  </div>
</template>

<script setup lang="ts">
interface Props {
  title: string
  description: string
  url: string
}

const props = defineProps<Props>()
const copied = ref(false)

const shareOnTwitter = () => {
  const text = `${props.title} - ${props.description}`
  const twitterUrl = `https://twitter.com/intent/tweet?text=${encodeURIComponent(text)}&url=${encodeURIComponent(props.url)}`
  window.open(twitterUrl, '_blank', 'width=550,height=420')
}

const shareOnLinkedIn = () => {
  const linkedInUrl = `https://www.linkedin.com/sharing/share-offsite/?url=${encodeURIComponent(props.url)}`
  window.open(linkedInUrl, '_blank', 'width=550,height=420')
}

const shareViaEmail = () => {
  const subject = encodeURIComponent(`Check out: ${props.title}`)
  const body = encodeURIComponent(`I thought you might be interested in this project:\n\n${props.title}\n${props.description}\n\n${props.url}`)
  const emailUrl = `mailto:?subject=${subject}&body=${body}`
  window.open(emailUrl)
}

const copyLink = async () => {
  try {
    await navigator.clipboard.writeText(props.url)
    copied.value = true
    setTimeout(() => {
      copied.value = false
    }, 2000)
  } catch (err) {
    // Fallback for older browsers
    const textArea = document.createElement('textarea')
    textArea.value = props.url
    document.body.appendChild(textArea)
    textArea.select()
    document.execCommand('copy')
    document.body.removeChild(textArea)
    copied.value = true
    setTimeout(() => {
      copied.value = false
    }, 2000)
  }
}
</script>
<template>
  <div class="min-h-screen bg-slate-950 text-slate-300">
    <div class="mx-auto w-full max-w-5xl px-4 py-8 sm:px-6 sm:py-12 md:px-8 lg:px-12">
      <NuxtLink to="/" class="text-xs sm:text-sm text-slate-400 hover:text-slate-200 transition-colors duration-200">← Back to home</NuxtLink>
      
      <div v-if="pending" class="mt-6 sm:mt-8 md:mt-10">
        <div class="animate-pulse">
          <div class="h-6 sm:h-8 bg-slate-800 rounded w-3/4 mb-3 sm:mb-4" />
          <div class="h-3 sm:h-4 bg-slate-800 rounded w-1/2 mb-4 sm:mb-6" />
          <div class="h-24 sm:h-32 bg-slate-800 rounded" />
        </div>
      </div>

      <div v-else-if="error || !project" class="mt-6 sm:mt-8 md:mt-10 px-2 sm:px-0">
        <Head>
          <Title>Project not found · Projects</Title>
          <Meta name="description" content="The project you are looking for does not exist." />
        </Head>
        <h1 class="text-xl sm:text-2xl font-semibold text-white">Project not found</h1>
        <p class="mt-2 text-sm sm:text-base text-slate-400">The project you are looking for does not exist.</p>
      </div>

      <div v-else class="mt-4 sm:mt-6">
        <Head>
          <Title>{{ `${project.title} · Projects` }}</Title>
          <Meta name="description" :content="project.description" />
        </Head>

        <div class="px-2 sm:px-0">
          <h1 class="text-2xl sm:text-3xl font-bold text-white leading-tight">{{ project.title }}</h1>
          <p class="mt-2 text-sm sm:text-base text-slate-400 leading-relaxed">{{ project.description }}</p>

          <ul v-if="project.tech" class="mt-3 sm:mt-4 flex flex-wrap gap-1.5 sm:gap-2">
            <li
              v-for="tech in project.tech"
              :key="tech"
              class="rounded border border-slate-800 bg-slate-900/40 px-2 py-1 text-xs text-slate-400"
            >
              {{ tech }}
            </li>
          </ul>

          <div v-if="project.image" class="mt-4 sm:mt-6 border-1 border-slate-900 rounded-lg overflow-hidden">
            <img
              :src="project.image"
              :alt="project.title"
              class="w-full rounded-lg"
            >
          </div>

          <div class="prose prose-invert prose-sm sm:prose-base mt-4 sm:mt-6 max-w-none">
            <ContentRenderer :value="project" />
          </div>

          <div class="mt-6 sm:mt-8 flex items-center gap-3">
            <NuxtLink
              v-if="project.externalUrl"
              :to="project.externalUrl"
              target="_blank"
              class="inline-flex items-center rounded-md border border-emerald-700 bg-emerald-600/10 px-3 py-1.5 text-xs sm:text-sm font-medium text-emerald-300 hover:bg-emerald-600/20 transition-colors duration-200"
            >
              Visit project
            </NuxtLink>
          </div>

          <!-- Social Share Buttons -->
          <div class="mt-6 sm:mt-8">
            <SocialShareButtons
              :title="project.title"
              :description="project.description"
              :url="shareUrl"
            />
          </div>
        </div>
      </div>
      <!--Divider
      <div class="mt-6 sm:mt-8">
        <div class="h-px bg-slate-900"></div>
      </div>-->
      <!--Other projects-->
      <div class="mx-auto w-full">
        <h2 class="text-xs sm:text-sm font-semibold uppercase tracking-widest text-emerald-400 mt-8 sm:mt-8">Other projects</h2>             
          <div class="mt-3 sm:mt-4 grid gap-4 sm:gap-6 grid-cols-1 sm:grid-cols-2">
            <!--limit the projects to 2 and add a see more button-->
                <NuxtLink
                v-for="otherProject in projects.slice(0, 2)"
                :key="otherProject.slug"
                :to="`/projects/${otherProject.slug}`"
                class="block"
              >
                <article
                  v-glow
                  class="mouse-glow rounded-md border border-slate-800 bg-slate-900/40 p-3 sm:p-4 transition hover:border-slate-700"
                >
                <img :src="otherProject.image" :alt="otherProject.title" class="w-full h-32 sm:h-40 object-cover rounded-md mb-2 sm:mb-3 border-1 border-slate-900" />
                  <h4 class="font-semibold text-sm sm:text-base text-slate-100 leading-snug">{{ otherProject.title }}</h4>
                  <p class="mt-1 text-xs sm:text-sm text-slate-400 line-clamp-2 sm:truncate leading-relaxed">{{ otherProject.description }}</p>
                  <div class="mt-2 text-xs text-slate-500 leading-relaxed">{{ otherProject.tech.join(' · ') }}</div>
                </article>
              </NuxtLink>
            </div>
            <div class="mt-4 sm:mt-6 flex items-center justify-start">
            <NuxtLink to="/#projects"
            class="text-xs sm:text-sm text-slate-400 hover:text-slate-200 transition-colors duration-200 px-4 py-2 rounded-md border border-slate-800 hover:border-slate-700 bg-slate-900/40 cursor-pointer">← See more</NuxtLink>
          </div>
      </div>
    </div>
    <footer class="mx-auto w-full max-w-5xl px-4 py-8 sm:px-6 sm:py-12 md:px-8 lg:px-12">
      <Footer />
    </footer>
  </div>
</template>

<script setup lang="ts">

import { getProjectBySlug, projects } from '@/data/projects'

const route = useRoute()
const slug = route.params.slug as string

// Get project from TypeScript data file instead of markdown
const projectData = getProjectBySlug(slug)

interface MarkdownContent {
  body: object
  title?: string
  description?: string
  [key: string]: unknown
}

// Use queryContent to get the markdown content for the body
const { data: markdownContent, pending, error } = await useAsyncData(`project-content-${slug}`, () => 
  queryContent(`/projects/${slug}`).findOne()
) as { data: Ref<MarkdownContent | null>, pending: Ref<boolean>, error: Ref<Error | null> }

// Combine TypeScript data with markdown content
const project = computed(() => {
  if (!projectData) return null
  return {
    ...projectData,
    body: markdownContent.value?.body || {},
    _path: `/projects/${slug}`,
    _id: slug,
    _type: 'markdown'
  }
})

// Generate share URL
const shareUrl = computed(() => {
  if (import.meta.client) {
    return window.location.href
  }
  return `https://alfonso-nakale.com/projects/${slug}`
})

useHead(() => {
  const siteUrl = import.meta.client ? window.location.origin : 'https://alfonso-nakale.com'
  const title = `${project.value?.title} · Projects`
  const description = project.value?.description
  const image = project.value?.image
  const url = `${siteUrl}/projects/${project.value?.slug}`
  const type = 'website'

  return {
    title,
    meta: [
      { name: 'description', content: description },
      { property: 'og:title', content: title },
      { property: 'og:description', content: description },
      { property: 'og:image', content: image },
      { property: 'og:url', content: url },
      { property: 'og:type', content: type },
      { property: 'og:url', content: url },
      { property: 'og:image', content: image },
      { property: 'og:type', content: type },
      { property: 'og:url', content: url },
      { property: 'og:image', content: image },
      { property: 'og:type', content: type },
    ]
  }
})
</script>
